let initialState=[]

const TodoReducer=(state= initialState,action)=>{
switch(action.type){
    case "TO_ADD":{
    return {lists :  [...state.list,  action.payload]}
    }
    default : {
 return{state}
    }
}
}
export default TodoReducer;
