import React, { useReducer } from 'react'
let InitialState=0;
let reducer=(state,action)=>{
     switch(action){
        case "Increment" :
            return state+1;
        case "Decrement" :
            return state-1;
     }
}
const Count = () => {
    useReducer(reducer,InitialState)
  return (
    <div>
        <button>Increment</button>
        <button>Decrement</button>
    </div>
  )
}

export default Count
