import React, { useState } from 'react'
import { useDispatch, useSelector} from 'react-redux'
import { ToAdd, ToDelete, RemoveAll, Update } from './Action/Action';

import STYLE from './todo.module.css'
const Todo = () => {
    let [todoAdd,setTodoAdd]=useState("")
    
    let Dispatch=useDispatch()
   let listData =  useSelector((state)=>state.list);
   
   
  return (
    <div className={STYLE.main}>
        AddList:<input type="text" onChange={(e)=>setTodoAdd(e.target.value)}/>
        <button onClick={()=>{Dispatch(ToAdd(todoAdd))}}>TodoAdd</button>
        <button onClick={()=>{Dispatch(RemoveAll())}}>ClearAll</button>
        {listData.map((x,index)=>{
      return <div key={index}>
        <li>{x}</li><button onClick={()=>{Dispatch(ToDelete(index))}}>delete</button>
      <button onClick={()=>{Dispatch(Update(index,todoAdd))}}>Update</button>
      </div>
        })}
    </div>
  )
}

export default Todo
